        if (loading of theTab) is false then exit repeat
    end repeat

-- Only execute on active ticket (since ZD has its own tabbing system)

	delay 0.1

	return execute theTab javascript " function getRequesterName() {

		// Get requester name from requester dropdown
		// Grab only the first substring
		// Assuming FIRSTNAME LASTNAME, this would give us the requester's first name
		// However, this will grab last name if the dropdown is formatted as LASTNAME FIRSTNAME

		let requesterName = activeTicket.querySelector(`[data-test-id='ticket-system-field-requester-select'] > div > div`)[0].innerText.split(' ');

		let requesterNameNormal = requesterName[0].toUpperCase() + requesterName.substring(1).toLowerCase()

		console.log(requesterNameNormal)

		// Note: return value has trailing CRLF
		// This needs to be trimmed in subsequent Alfred steps
		// For some reason, this Applescript isn't playing nice with trimming 

		return requesterNameNormal
		}

		getRequesterName()

		"

end tell

