tell application "Google Chrome"

	activate

-- Only execute on active tab

	set theTab to active tab of window 1

-- Only execute if tab is done loading
    repeat
        if (loading of theTab) is false then exit repeat
    end repeat

-- Only execute on active ticket (since ZD has its own tabbing system)
    execute theTab javascript "

		document.querySelector(`[data-test-id='views_icon']`).click();

		// Click views refresh button
		document.querySelector(`[data-view-id='${ {query} }']`).click();

	"
	delay 1

-- Get ticket rows with TODAY's date

    return execute theTab javascript "

		// Helper function so that we can compare TODAY to the 'Assignee Updated' date

		function convertDateToUsLocale(date) {
			return date.toLocaleString('en-US', {month: 'short', day: 'numeric', year: 'numeric'})
		}

		todayAlfred = convertDateToUsLocale(new Date())

		function getTodaysTicketCount() {
			// Get ticket rows

			ticketRowsAlfred = Array.from(document.querySelectorAll(`#views-ticket-table-1500026128162-container [data-garden-id='tables.body'] > tr`))

			// Filter ticket rows – get only ticket rows with today's date

			ticketRowsFilteredAlfred = ticketRowsAlfred.filter(row => {
				let assigneeUpdatedDateAlfred = convertDateToUsLocale(new Date(row.querySelector(`td:last-child > time`).getAttribute('datetime')))

				return assigneeUpdatedDateAlfred === todayAlfred
		})

			// Final return value is length of filtered ticket array

			console.log('Ticket count (today): ' + ticketRowsFilteredAlfred.length)
			return ticketRowsFilteredAlfred.length
		}

		getTodaysTicketCount()
	"

end tell

