tell application "Google Chrome"

	activate

-- Only execute on active tab
	set theTab to active tab of window 1

-- Only execute if tab is done loading
    repeat
        if (loading of theTab) is false then exit repeat
    end repeat

-- Only execute on active ticket (since ZD has its own tabbing system)
    execute theTab javascript "

		// Define global variable for the ticket tab toolbar

		if (typeof alfredToolbar === 'undefined') {
			alfredToolbar = document.querySelectorAll(`[data-test-id='header-toolbar']`)[0]
		}

		// If workflow was never run before, define global variable alfredSearchTab
		// Else, (re)define alfredSearchTab according to whatever comes up from the querySelectorAll method (could be an empty NodeList, could be the search tab if present)

		if (typeof alfredSearchTab === 'undefined') {
			alfredSearchTab = document.querySelectorAll('.ember-view.workspace.search:not(.is-cached)')
		}
		else {
			alfredSearchTab = document.querySelectorAll('.ember-view.workspace.search:not(.is-cached)')
		}

		// Get current active ticket 

		if (typeof tickets === 'undefined') {
			let tickets = document.getElementsByClassName('ember-view workspace')
			let ticketsArray = Array.from(tickets)

			activeTicket = ticketsArray.filter(ticket => ticket.style.length === 0)[0]
		}

		// If Search tab is open, close it
		// Else, close the active ticket

		if (alfredSearchTab.length > 0 && (alfredSearchTab[0].style.length === 0 || alfredSearchTab[0].style.display === 'block')) {
			alfredToolbar.querySelectorAll(`[data-entity-type='search'] > button`)[0].click()
		}
		else {
			// Get current ticket number 
			currentTicketNumber = activeTicket.querySelectorAll('section > div')[5].getAttribute('data-side-conversations-modal-id')

			// Close current ticket tab

			alfredToolbar.querySelectorAll(`[data-entity-id='${currentTicketNumber}'] > button`)[0].click();
		}
	"

end tell

tell application "Google Chrome"

	activate

-- Only execute on active tab

	set theTab to active tab of window 1

-- Only execute if tab is done loading
    repeat
        if (loading of theTab) is false then exit repeat
    end repeat

-- Only execute on active ticket (since ZD has its own tabbing system)
    return execute theTab javascript " function getRequesterName() {
		if (typeof tickets === 'undefined') {
			let tickets = document.getElementsByClassName('ember-view workspace')
			let ticketsArray = Array.from(tickets)

			activeTicket = ticketsArray.filter(ticket => ticket.style.length === 0)[0]
		}

		// Get requester name from requester dropdown
		// Grab only the first substring
		// Assuming FIRSTNAME LASTNAME, this would give us the requester's first name
		// However, this will grab last name if the dropdown is formatted as LASTNAME FIRSTNAME

		let requesterName = activeTicket.querySelectorAll(`[data-test-id='ticket-system-field-requester-select'] > div > div`)[0].innerText.split(' ')[0];

		let requesterNameNormal = requesterName[0].toUpperCase() + requesterName.substring(1).toLowerCase()

		console.log(requesterNameNormal)

		// Note: return value has trailing CRLF
		// This needs to be trimmed in subsequent Alfred steps
		// For some reason, this Applescript isn't playing nice with trimming 

		return requesterNameNormal
		}

		getRequesterName()

		"

end tell

